import React from 'react'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography'
import Button from  '@material-ui/core/Button'
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import PictureModal from './PictureModal';
// import ButtonGroup from '@material-ui/core/ButtonGroup'

const useStyles = makeStyles((theme) => ({
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 5,
    },
    slider: {
        width: 300,
        backgroundColor: 'white',
      },
  })
);

export default ({clearGrid, updateColumns, columns, updateRows, rows, resizeGrid, size}) => {

    const classes = useStyles();

    return (
        <AppBar>
        <Toolbar>

          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>

          <Typography variant="h6" className={classes.title}>
            Welcome!
          </Typography>

          <Toolbar>
              <Typography variant="h6">
                  Change rows
              </Typography>
              <input type="number" min="10" max="40" onChange={updateRows} value={rows}></input>
          </Toolbar>

          <Toolbar>
              <Typography variant="h6">
                  Change columns
              </Typography>
              <input type="number" min="10" max="40" onChange={updateColumns} value={columns}></input>
            </Toolbar>

            <Toolbar>
              <Typography variant="h6">
                  Grid Size
              </Typography>
              <input type="range" min="1.5" max="3.5" step='.1' onChange={resizeGrid} value={size}></input>
            </Toolbar>

          <Button color="inherit" onClick={clearGrid}>Clear</Button>

          <PictureModal></PictureModal>

        </Toolbar>
      </AppBar>

    )

}
