import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import html2canvas from 'html2canvas'
import Button from  '@material-ui/core/Button'

function getModalStyle() {
  return {
    top: `50%`,
    left: `50%`,
    transform: `translate(-50%, -50%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  image: {
      width: 400
  }
}));

export default ({ show, handleClose }) => {

  const showHideClassName = show ? 'modal display-block' : 'modal display-none';

  const classes = useStyles();
  const [modalStyle] = useState(getModalStyle);
  const [open, setOpen] = useState(false);
  const [dataURL, setDataURL] = useState('');

  const handleOpen = () => {
    setOpen(true);
    html2canvas(document.getElementById('grid'))
    .then(canvas => {
        setDataURL(canvas.toDataURL())
    });
  };

  // const handleClose = () => {
  //   setOpen(false);
  // };

  const body = (
    <div className={showHideClassName} className={classes.paper}>
        <img id="image" src={dataURL} className={classes.image} alt="MathMaker.png"></img>
        <Button type='button'>
            <a href={dataURL} download={"MathMaker.png"}>Click To Download</a>
        </Button>
        <button onClick={handleClose} ></button>
    </div>
  );

  return (
    <div>

    {body}




      {/* <Button onClick={handleOpen}>Take Picture</Button>

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal> */}
    </div>
  );
}
