import React,  { useState, useEffect } from 'react'
import '../stylesheets/Login.scss'

export default () => {

    const [hasAccount, setHasAccount] = useState(false)

    return (
        <div class="login-card">
            <h1>{hasAccount ? 'Log-in' : 'Sign-up'}</h1>
            <form>
                <input type="text" name="user" placeholder="Username"/>
                <input type="password" name="pass" placeholder="Password"/>
                <input type="submit" name="login" class="login login-submit" value={hasAccount ? 'Log-in' : 'Sign-up'}/>
            </form>
            <h3>
                {hasAccount ? 'Dont have an ccount?' : 'Have an Account?'} 
                <button onClick={() => setHasAccount(!hasAccount)}>
                {hasAccount ? 'Log-in' : 'Sign-up'}
                </button>
            </h3>
        </div>
    )

}
