import React, { Component } from 'react';
import './stylesheets/App.scss';
import Grid from './components/Grid'
import Calculator from './components/Calculator';
import Header from './components/Header'
import Footer from './components/Footer'
import { array } from 'prop-types';


class App extends Component {

  state = {
    gridData: [],
    columns: 20,
    rows: 20
  }

  createGrid = () => {
    const col = this.state.columns
    const row = this.state.rows
    const gridData = []
    for(let i=0;i<(col*row);i++) {
      const x = i % col
      const y = Math.floor(i / col)
      const position = `${x},${y}`
      gridData[i] = {position, value:''}
    }
    this.setState({gridData})
  }

  updategrid = (colm) => {
    const oldGridData = this.state.gridData
    const col = colm
    const row = this.state.rows
    const newGridData = {}
    for(let i=0;i<(col*row);i++) {
      const x = i % col
      const y = Math.floor(i / col)
      const position = `${x},${y}`
      newGridData[position] = ''
      // if(oldGridData[i].position){

      // }

    }
    const arrayOfObj = Object.entries(newGridData)  //.map((e) => ( { [e[0]]: e[1] } ));
    console.log(arrayOfObj)
    // this.setState({gridData: newGridData})
  }

  componentDidMount() {
    this.createGrid()
  }

  updateColumns = (event) => {
    this.setState({columns: parseInt(event.target.value)})
    this.updategrid(event.target.value)
  }

  updateCell = (position, value) => {
    const newGridData = this.state.gridData.map(cell => {
      if(cell.position === position) {
        return {position, value}
      } else {
        return cell
      }
    })
    this.setState({gridData: newGridData})
  }

  render() {
    return (
      <>
        <Header clearGrid={this.createGrid} updateColumns={this.updateColumns} columns={this.state.columns} ></Header>
        <main>
          <Calculator ></Calculator>
          <Grid gridData={this.state.gridData} updateCell={this.updateCell} columns={this.state.columns} ></Grid>
        </main>
        <Footer></Footer>
      </>
    ) 
  }


  // render() {
  //   return (
  //     <>
  //       <Header></Header>
  //       <MathMaker></MathMaker>
  //       <Footer></Footer>
  //     </>

  //   );
  // }
}

export default App;
