import Modal from '@material-ui/core/Modal';
import html2canvas from 'html2canvas'
import Button from  '@material-ui/core/Button'

function getModalStyle() {
  return {
    top: `50%`,
    left: `50%`,
    transform: `translate(-50%, -50%)`,
  };
}

