import React from 'react'
import html2canvas from 'html2canvas'

export default () => {

    const handleClick = (event) => {
        html2canvas(document.getElementById('grid'))
            .then(canvas => {
                var image = canvas.toDataURL("image/jpg");
                console.log(image)
        });
    }

    return (
        <header>
            <h1>Welcome User!</h1>

            <nav>
                <h2>Clear</h2>
                <h2>Save</h2>
                <h2 onClick={handleClick}>Take pic</h2>
                <h2>Logout</h2>
            </nav>
        </header>
    )

}
