import React, { Component } from 'react';
import './stylesheets/App.scss';
import Grid from './components/Grid'
import Calculator from './components/Calculator';
import Header from './components/Header'
import Footer from './components/Footer'


class App extends Component {

  state = {
    gridData: [],
    columns: 30,
    rows: 20
  }

  createGrid = () => {
    const col = this.state.columns
    const row = this.state.rows
      const gridData = []
      for(let i=0;i<(col*row);i++) {
          const x = i % col
          const y = Math.floor(i / col)
          const position = `${x},${y}`
          gridData[i] = {position, value:''}
      }
    this.setState({gridData})
  }

  componentDidMount() {
    this.createGrid()
  }

  updateColumns = (event) => {
    this.setState.columns({columns: event.target.value})
  }

  updateCell = (position, value) => {
    const newGridData = this.state.gridData.map(cell => {
      if(cell.position === position) {
        return {position, value}
      } else {
        return cell
      }
    })
    this.setState({gridData: newGridData})
  }

  render() {
    return (
      <>
        <Header clearGrid={this.createGrid} updateColumns={this.updateColumns}></Header>
        <main>
          <Calculator ></Calculator>
          <Grid gridData={this.state.gridData} updateCell={this.updateCell}></Grid>
        </main>
        <Footer></Footer>
      </>
    ) 
  }


  // render() {
