import React, {useState} from 'react'
import Cell from './Cell'
// import Input from './Input'

export default () => {

    const [text, settext] = useState('butts')

    const handleChange = (event) => {
        settext('butts + 1')
    }

    const creategrid = () => {
        const cells = []
        for(let i=0; i<=1575; i++) {
            cells.push(<Cell key={i} ></Cell>)
        }
        return cells
    }

    return (
        <>
            <input type='text' onChange={handleChange(event)} value={text}></input>
            <div className='grid'>
                {creategrid()}
            </div>
        </>
    )

}


